import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tempUsers, setTempUsers] = useState([]); // Initialize as an empty array
  const [error, setError] = useState('');

  // Fetch temp users for approval
  useEffect(() => {
    const fetchTempUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/temp-users');
        const data = response.data;

        // Check if the response is an array
        if (Array.isArray(data)) {
          setTempUsers(data);
        } else {
          console.error('Expected an array, but got:', data); // Log unexpected response
          setTempUsers([]); // Set to empty array if not an array
        }
      } catch (error) {
        console.error('Error fetching temp users:', error);
        setError('Error fetching temporary users');
      }
    };

    fetchTempUsers();
  }, []);

  const handleApproveUser = async (id) => {
    try {
      await axios.post('http://localhost:5000/api/approve-user', { id });
      // Fetch temp users again after approving a user
      fetchTempUsers();
    } catch (error) {
      console.error('Error approving user:', error);
    }
  };

  const handleRejectUser = async (id) => {
    try {
      await axios.post('http://localhost:5000/api/reject-user', { id });
      // Fetch temp users again after rejecting a user
      fetchTempUsers();
    } catch (error) {
      console.error('Error rejecting user:', error);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      <div className="flex-1 p-6 overflow-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-6">Temporary Users for Approval</h1>

        {error && <div className="text-red-500">{error}</div>}

        {/* Render temp users if they exist and are an array */}
        {Array.isArray(tempUsers) && tempUsers.length > 0 ? (
          <table className="min-w-full bg-white rounded-lg shadow-lg">
            <thead className="bg-gray-200 text-gray-700 uppercase text-sm">
              <tr>
                <th className="py-3 px-6 text-left">Name</th>
                <th className="py-3 px-6 text-left">Email</th>
                <th className="py-3 px-6 text-left">Role</th>
                <th className="py-3 px-6 text-left">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-900">
              {tempUsers.map((tempUser) => (
                <tr key={tempUser._id} className="border-b">
                  <td className="py-3 px-6">{tempUser.name}</td>
                  <td className="py-3 px-6">{tempUser.email}</td>
                  <td className="py-3 px-6">{tempUser.role}</td>
                  <td className="py-3 px-6 space-x-2 flex">
                    <button
                      onClick={() => handleApproveUser(tempUser._id)}
                      className="bg-blue-500 text-white py-1 px-2 rounded hover:bg-blue-600"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleRejectUser(tempUser._id)}
                      className="bg-red-500 text-white py-1 px-2 rounded hover:bg-red-600"
                    >
                      Reject
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No temporary users found.</p>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
