import React, { useState } from 'react';

const AddCaseForm = () => {
  const [petitionerFirstName, setPetitionerFirstName] = useState('');
  const [petitionerLastName, setPetitionerLastName] = useState('');
  const [address, setAddress] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [dateOfFiling, setDateOfFiling] = useState('');
  const [caseType, setCaseType] = useState('');
  const [caseStatus, setCaseStatus] = useState('');
  const [caseDescription, setCaseDescription] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission here, e.g., send data to backend
    console.log({
      petitionerFirstName,
      petitionerLastName,
      address,
      phoneNumber,
      dateOfFiling,
      caseType,
      caseStatus,
      caseDescription,
    });
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <h2 className="text-2xl font-bold mb-4">Add Cases</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="petitionerFirstName">
            Petitioner First Name
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="petitionerFirstName"
            type="text"
            placeholder="Enter your first name"
            value={petitionerFirstName}
            onChange={(e) => setPetitionerFirstName(e.target.value)}
          />
        </div>
        {/* Similar input fields for other form fields */}
        {/* ... */}
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="submit"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddCaseForm;
